     and s.dept_name = #{deptName,jdbcType=CHAR}

     and s.date  >= #{date1,jdbcType=CHAR}


     and s.date <  #{date2,jdbcType=CHAR}

   select 

	from initialize_info 
   where isDeleted = 0 and   store_id = #{storeId}
