mapper.update:
update coupon_info
set  is_type =#{isType}
where coupon_id = #{couponId}

mapper.select:
select   i.* from coupon_store s  left join  coupon_info i on s.coupon_id=i.coupon_id 
where  i.is_delete = 0   and 
CURDATE() <= i.coupon_stop_time AND CURDATE() >= i.release_time 
 and i.start_type = #{startType} 
 and i.store_account = #{storeAccount}
group by i.coupon_id
