mapper.select:
select 

from goods_stock
where a_id = #{aId,jdbcType=INTEGER}
  and store_id = #{storeId,jdbcType=INTEGER}

mapper.delete:
delete from goods_stock
where a_id = #{aId,jdbcType=INTEGER}
  and store_id = #{storeId,jdbcType=INTEGER}

mapper.update:
update goods_stock


    count = #{count,jdbcType=INTEGER},


    update_time = #{updateTime,jdbcType=CHAR},


where a_id = #{aId,jdbcType=INTEGER}
  and store_id = #{storeId,jdbcType=INTEGER}

update goods_stock
set count = #{count,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=CHAR}
where a_id = #{aId,jdbcType=INTEGER}
  and store_id = #{storeId,jdbcType=INTEGER}
