mapper.insert:
insert into initialize_info (id, dept_name, goods_price, 
  type, type_name, note, date, 
  incometype_type, isDeleted, store_id,price_name
  )
values (#{id,jdbcType=INTEGER}, #{deptName,jdbcType=CHAR}, #{goodsPrice,jdbcType=INTEGER}, 
  #{type,jdbcType=CHAR}, #{typeName,jdbcType=CHAR}, #{note,jdbcType=CHAR}, #{date,jdbcType=CHAR}, 
  #{incometypeType,jdbcType=INTEGER}, #{isdeleted,jdbcType=INTEGER}, #{storeId,jdbcType=INTEGER}, #{priceName,jdbcType=CHAR}
  )

mapper.select:
     select 
   i.id, d.dept_name, i.goods_price, i.type, i.type_name, i.note, i.date, i.incometype_type, i.isDeleted, i.store_id, i.price_name
	from initialize_info i left join  dept_info d ON i.dept_name=d.dept_id
   where i.isDeleted = 0 and i.store_id = #{params.storeId}

           and i.type = #{params.type,jdbcType=CHAR}



          and i.dept_name = #{params.deptName,jdbcType=CHAR}



         and i.price_name = #{params.priceName,jdbcType=CHAR}



       and i.date  >= #{params.date1,jdbcType=CHAR}



       and i.date <=  #{params.date2,jdbcType=CHAR}
