mapper.select:
	select 
  from project_commission where 1 = 1 and is_deleted = 0 

      and project_id = #{projectId,jdbcType=INTEGER}


      and level_id = #{levelId,jdbcType=INTEGER}


      and assign_cash_type = #{assignCashType,jdbcType=INTEGER}


      and assign_cash = #{assignCash,jdbcType=INTEGER}



      and assign_card = #{assignCard,jdbcType=INTEGER}



      and is_deleted = #{isDeleted,jdbcType=INTEGER}



      and commission_no_gold= #{commissiNoGold,jdbcType=INTEGER},


       and commission_gold= #{commissiGold,jdbcType=INTEGER},


       and  commission_no_course= #{commissionNoCourse,jdbcType=INTEGER},


       and   commission_course = #{commissionCourse,jdbcType=INTEGER},


       and   commission_no_cash = #{commissionNoCash,jdbcType=INTEGER},


       and   commission_cash = #{commissionCash,jdbcType=INTEGER},

mapper.insert:
insert into project_commission ( shift_mahjong_id, project_id, level_id, assign_cash_type, assign_cash, assign_card_type, assign_card, non_assign_cash_type, non_assign_cash, non_assign_card_type, non_assign_card, appointment_reward_type, appointment_reward, is_deleted, create_time, update_time, last_operator_id ) values
( #{item.shiftMahjongId,jdbcType=INTEGER}, #{item.projectId,jdbcType=INTEGER}, #{item.levelId,jdbcType=INTEGER}, #{item.assignCashType,jdbcType=INTEGER}, #{item.assignCash,jdbcType=INTEGER}, #{item.assignCardType,jdbcType=INTEGER}, #{item.assignCard,jdbcType=INTEGER}, #{item.nonAssignCashType,jdbcType=INTEGER}, #{item.nonAssignCash,jdbcType=INTEGER}, #{item.nonAssignCardType,jdbcType=INTEGER}, #{item.nonAssignCard,jdbcType=INTEGER}, #{item.appointmentRewardType,jdbcType=INTEGER}, #{item.appointmentReward,jdbcType=INTEGER}, #{item.isDeleted,jdbcType=INTEGER}, #{item.createTime,jdbcType=CHAR}, #{item.updateTime,jdbcType=CHAR}, #{item.lastOperatorId,jdbcType=INTEGER}
)

mapper.select:
  	select pc.*,el.level_name from project_commission pc
	left join employee_level el on pc.level_id = el.level_id
    where pc.project_id = #{projectId,jdbcType=INTEGER}

select a.level_id from project_commission a 
where exists(
              select 1 from project_step b where b.project_id = a.project_id and b.is_deleted = 0 and b.project_step_id = #{projectStepId,jdbcType=INTEGER}
            ) and a.is_deleted = 0;
